mapper.delete:
update reserve
set status='已取消'
where id = #{reserveId};
update room
set status='空房'
where id=(
    select room
    from roomregister
    where id = (select roomregister from reserve where id = #{reserveId})
);

mapper.select:
select *
from reserve r
where r.id=#{reserveId}

select *
from reserve r,roomregister rg,room ro
where r.roomRegister=rg.id and rg.room=ro.id and ro.id=#{roomId}
